import React, { useState } from 'react';
import { Download, Send, Loader2 } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from './components/ui/alert';
import { Button } from './components/ui/button';
import { Input } from './components/ui/input';
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from './components/ui/card';

const NeuroCraftImageForge = () => {
  const [prompt, setPrompt] = useState('');
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generateImage = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('https://api-inference.huggingface.co/models/black-forest-labs/FLUX.1-dev', {
        method: 'POST',
        headers: { 'Authorization': 'Bearer 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','Content-Type': 'application/json' },
        body: JSON.stringify({ inputs: prompt }),
      });
      if (!response.ok) throw new Error('Failed to generate image');
      const blob = await response.blob();
      setImage(URL.createObjectURL(blob));
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const downloadImage = () => {
    if (image) {
      const link = document.createElement('a');
      link.href = image;
      link.download = 'neurocraft_image.png';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 p-8 flex items-center justify-center">
      <Card className="w-full max-w-2xl bg-gray-800 border-cyan-500 border-2">
        <CardHeader>
          <CardTitle className="text-3xl font-bold text-center text-cyan-400">
            NeuroCraft: AI Image Forge
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex space-x-2">
            <Input
              type="text"
              placeholder="Enter your image prompt..."
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              className="flex-grow bg-gray-700 border-cyan-500 text-gray-100"
            />
            <Button onClick={generateImage} disabled={loading} className="bg-cyan-600 hover:bg-cyan-700">
              {loading ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : <Send className="mr-2 h-4 w-4" />}
              Generate
            </Button>
          </div>
          {error && (
            <Alert variant="destructive">
              <AlertTitle>Error</AlertTitle>
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
          {image && (
            <div className="mt-4 relative group">
              <img src={image} alt="Generated" className="w-full rounded-lg shadow-lg" />
              <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <Button onClick={downloadImage} className="bg-cyan-600 hover:bg-cyan-700">
                  <Download className="mr-2 h-4 w-4" />
                  Download
                </Button>
              </div>
            </div>
          )}
        </CardContent>
        <CardFooter className="text-center text-gray-400">
          Powered by FLUX.1-dev model
        </CardFooter>
      </Card>
    </div>
  );
};

export default NeuroCraftImageForge;
